import React from "react";

function App() {
    let [cards, setCards] = React.useState([])
    let [tabs, setTabs] = React.useState([])

    function tabsList(props) {
        let tabs = []
        return (
            tabs.map((tabTitle) => {
                return (<button className='tab' onClick={() => console.log(tabTitle)}>{tabTitle}</button>)
            }))
    }

    function cardList(props) {
        let cards = []
        return (
            cards.map((card, index) => {
                if (index === 3) {
                    return <span><br/>
                        {card}
                    </span>
                }
                return (
                    <span>{card}</span>
                )
            })
        )
    }

    return (
        <div className='content'>
            <h2 className='header'>Авторские туры по регионам</h2>
            <p className='description'>От похода в гору до пешеходной прогулки.<br/>От круиза до погружения в виртуальную реальность. Туры для каждого.</p>
            <div className='tabRow'> {tabsList()} </div>
            {cardList()}
        </div>
    )
}

export default App;
