import React, {useEffect, useState} from "react"

const App = () => {
    const [tabs, setTabs] = useState([])
    const [activeTabIndex, setActiveTabIndex] = useState(-1)
    const [cards, setCards] = useState([])

    useEffect(() => {
        fetch('https://rsttur.ru/api/collections/author-tour-region-tabs?city_id=48')
            .then(response => response.json())
            .then(data => {
                setTabs(data.data)
            })
    })

    return (
        <div className='content'>
            <h2 className='header'>Авторские туры по регионам</h2>
            <p className='description'>От похода в гору до пешеходной прогулки.<br/>От круиза до погружения в
                виртуальную реальность. Туры для каждого.</p>
            <div className='tabRow'> {tabs.map((tab, index) => {
                if (index === activeTabIndex)
                    return <button key={tab.id} style={{
                        border: '1px solid #387CCC',
                        boxSizing: 'border-box',
                        borderRadius: '33px'
                    }}>{tab.name}</button>
                return <button key={tab.id} onClick={() => {
                    setActiveTabIndex(index)
                    fetch('https://rsttur.ru/api/collections/author-tour?city_id=48&count=6&region_id=' + tab.params.value)
                        .then(response => response.json())
                        .then(data => {
                            console.log(data.data)
                            setCards(data.data)
                        })
                }
                }> {tab.name} </button>
            })} </div>
            <div>
                {cards.map((card) => {
                    return <div key={card.id}>{card.title}</div>
                })}
            </div>
        </div>
    )
}

export default App;
