import React, {useEffect, useState} from "react"
import './App.css'

const App = () => {
    const [tabs, setTabs] = useState([])
    const [activeTabIndex, setActiveTabIndex] = useState(-1)
    const [cards, setCards] = useState([])

    useEffect(() => {
        fetch('https://rsttur.ru/api/collections/author-tour-region-tabs?city_id=48')
            .then(response => response.json())
            .then(data => {
                setTabs(data.data)

                for (let i = 0; i < data.data.length; i++) {
                    if (data.data[i].active) {
                        setActiveTabIndex(i)
                        break
                    }
                }
            })
    })

    const clickOnButton = (index, tab) => {
        setActiveTabIndex(index)
        console.log("Request: " + tab.params[0].value)
        fetch('https://rsttur.ru/api/collections/author-tour?city_id=48&count=6&region_id=' + tab.params[0].value)
            .then(response => response.json())
            .then(data => {
                console.log(data.data)
                setCards(data.data)
            })
    }

    return (
        <div className='content'>
            <h2 className='header'>Авторские туры по регионам</h2>
            <p className='description'>От похода в гору до пешеходной прогулки.<br/>От круиза до погружения в
                виртуальную реальность. Туры для каждого.</p>
            <div className='tabRow'> {tabs.map((tab, index) => {
                if (index === activeTabIndex)
                    return <button key={tab.id} className="activeButton">{tab.name}</button>
                return <button key={tab.id} className="myButton"
                               onClick={() => {clickOnButton(index, tab)}}>
                    {tab.name}
                </button>
            })} </div>
            <div>
                {cards.map((card) => {
                    console.log(card)
                    return <div key={card.id} >
                        <img src={'https://rsttur.ru' + card.image.lg} alt={""} className='image'/>

                        {card.title}
                    </div>
                })}
            </div>
        </div>
    )
}

export default App;
